CREATE TABLE IF NOT EXISTS `owneddrugslabs` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `labid` int(15) NOT NULL DEFAULT 0,
  `owner` varchar(255) NOT NULL DEFAULT 'none',
  `type` varchar(255) NOT NULL DEFAULT 'unknow',
  `employees` longtext DEFAULT '[]',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci ROW_FORMAT=DYNAMIC;


CREATE TABLE IF NOT EXISTS `drugsdeliveries` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `type` varchar(50) DEFAULT NULL,
  `coords` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=89 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci ROW_FORMAT=DYNAMIC;

